package com.auth.entity.dao;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class Authority implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**    * 启用    */    private Integer enabled;    /**    * 资源名    */    private String name;    /**    * 资源描述    */    private String description;}