package com.auth.entity.dao;import com.baomidou.mybatisplus.annotation.FieldFill;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class User implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**    * 启用    */    private Integer enabled;    /**    * 用户名    */    private String username;    /**    * 业务名(认证中心存储多种用户类型时,防止出现用户名重复情况,可以通过用户所属业务名加以区分)    */    private String serviceName;    /**    * 创建时间    */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**    * 更新时间    */    @TableField(fill = FieldFill.INSERT_UPDATE)    private LocalDateTime updateTime;}