package com.auth.entity.dto;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class AuthorityDto implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    private Integer id;    /**     * 启用     */    private Integer enabled;    /**     * 资源名     */    private String name;    /**     * 资源描述     */    private String description;}