package com.auth.entity.dto;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class UserDto implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    private Integer id;    /**     * 启用     */    private Integer enabled;    /**     * 用户名     */    private String username;    /**     * 业务名(认证中心存储多种用户类型时,防止出现用户名重复情况,可以通过用户所属业务名加以区分)     */    private String serviceName;    /**     * 创建时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime createTime;    /**     * 更新时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime updateTime;}