package com.auth.entity.vo;import io.swagger.annotations.ApiModelProperty;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotNull;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class AuthorityVo implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 启用     */    @ApiModelProperty("启用")    private Integer enabled=0;    /**     * 资源名     */    @ApiModelProperty("资源名")    @NotBlank(message = "资源名不能为空")    @NotNull(message = "资源名不能为空")    private String name;    /**     * 资源描述     */    @ApiModelProperty("资源描述")    private String description;    @Override    public String toString() {        return "资源名: "+name+" ,资源描述: "+description+" 启用资源: "+((enabled==1)?"是":"否");    }}