package com.auth.entity.vo;import io.swagger.annotations.ApiModelProperty;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotEmpty;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class ManagerVo implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 账号     */    @ApiModelProperty("账号")    @NotBlank(message = "账号不能为空")    @NotEmpty(message = "账号不能为空")    private String account;    /**     * 密码     */    @ApiModelProperty("密码")    @NotBlank(message = "密码不能为空")    @NotEmpty(message = "密码不能为空")    private String pwd;    /**     * 是否启用     */    @ApiModelProperty("是否启用")    private Integer enabled=0;    @Override    public String toString() {       return "管理员账号: "+account+" ,密码为: "+pwd+" ,启用管理员: "+((enabled==1)?"是":"否");    }}