package com.course.entity.dao;import com.baomidou.mybatisplus.annotation.FieldFill;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.course.entity.vo.UpLoadFileVo;import com.course.exception.CourseException;import com.course.service.ICourseService;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class Course implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**    * 课程名字    */    private String courseName;    /**     * 课程存储桶名     */    private String courseBucket;    /**     * 课程文件目录     */    private String courseDir;    /**    * 课程封面路径    */    private String courseCoverPath;    /**    * 课程文件路径    */    private String courseFilePath;    /**    * 大分类类型    */    private Integer bigType;    /**    * 小分类类型    */    private Integer smallType;    /**    * 课程是否启用    */    private Integer enable;    /**     * 创建时间     */    @TableField(fill = FieldFill.INSERT)    private LocalDateTime createTime;    /**     * 更新时间     */    @TableField(fill = FieldFill.INSERT_UPDATE)    private LocalDateTime updateTime;    public static void repeatNameCheck(String courseName,ICourseService iCourseService) {        long count = iCourseService.count(new QueryWrapper<Course>()                .eq("course_name", courseName));        if(count>0){            throw new CourseException("课程名重复");        }    }    public void fill(UpLoadFileVo upLoadFileVo) {        this.courseBucket=upLoadFileVo.getBucketName();        this.courseDir=upLoadFileVo.getPath();        this.courseCoverPath=upLoadFileVo.getCourseCoverFileUrl();        this.courseFilePath=upLoadFileVo.getCourseFileUrl();    }}