package com.course.entity.dto;import com.fasterxml.jackson.annotation.JsonFormat;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import java.io.Serializable;import java.time.LocalDateTime;import java.util.List;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class CourseDto implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    private Integer id;    /**     * 课程名字     */    private String courseName;    /**     * 课程封面路径     */    private List<String> courseCoverPath;    /**     * 课程文件路径     */    private String courseFilePath;    /**     * 大分类类型     */    private BigCourseTypeDto bigType;    /**     * 小分类类型     */    private SmallCourseTypeDto smallType;    /**     * 课程是否启用     */    private Integer enable;    /**     * 创建时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime createTime;    /**     * 更新时间     */    @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    private LocalDateTime updateTime;}