package com.user.entity.dao;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.security.domain.BaseUserDetails;import com.user.constants.UserServiceConstants;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import org.springframework.security.core.context.SecurityContextHolder;import java.io.Serializable;import java.time.LocalDateTime;import static com.security.verfiyTokenFilter.RpcTokenAuthenticationFilter.SEPARATOR;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class User extends BaseUserDetails implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**    * 用户名    */    private String name;    /**    * 密码    */    private String pwd;    /**    * 用户所属校区id    */    private Integer cId;    /**    * 用户信息    */    private String info;    /**    * 启用用户    */    private Integer enable;    /**    * 创建时间    */    private LocalDateTime createTime;    /**    * 更新时间    */    private LocalDateTime updateTime;    public static User getVisit() {       return (User) SecurityContextHolder.getContext().getAuthentication().getPrincipal();    }    @Override    public String getPassword() {        return pwd;    }    @Override    public String getUsername() {        return UserServiceConstants.USER_SERVICE_NAME+SEPARATOR+name;    }    @Override    public boolean isEnabled() {        return enable==1;    }}