package com.user.entity.dao;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.user.exception.UserException;import com.user.service.IUserFreeListenerService;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import java.io.Serializable;import java.time.LocalDateTime;@AllArgsConstructor@NoArgsConstructor@Data@Builderpublic class UserFreeListener implements Serializable {    private static final long serialVersionUID = 1L;    /**    * id    */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**    * 电话号码    */    private String phoneNumber;    /**    * 用户信息    */    private String info;    /**    * 创建时间    */    private LocalDateTime createTime;    /**    * 更新时间    */    private LocalDateTime updateTime;    public static void repeatCheck(String phoneNumber, IUserFreeListenerService iUserFreeListenerService) {        long count = iUserFreeListenerService.count(new QueryWrapper<UserFreeListener>().eq("phone_number", phoneNumber));        if(count>0){            throw new UserException("你已经注册过了");        }    }}