package com.user.entity.vo;import com.validator.NotEmptyCheck;import com.validator.RangeCheck;import io.swagger.annotations.ApiModelProperty;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import javax.validation.constraints.Positive;import java.io.Serializable;@AllArgsConstructor@NoArgsConstructor@Data@Builder@Slf4jpublic class UserVo implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 用户名     */    @ApiModelProperty("用户名")    @NotEmptyCheck(message = "用户名不能为空")    private String name;    /**     * 密码     */    @ApiModelProperty("密码")    @NotEmptyCheck(message = "密码不能为空")    private String pwd;    /**     * 用户所属校区id     */    @ApiModelProperty("用户所属校区id")    @Positive(message = "非法参数")    private Integer cId;    /**     * 用户信息     */    @ApiModelProperty("用户信息")    private String info;    /**     * 启用用户     */    @ApiModelProperty("启用用户")    @RangeCheck(range = {1,0},message = "非法参数")    private Integer enable;}